import { createSlice } from "@reduxjs/toolkit";

const initialState = {
  user: "as",
};

const userSlice = createSlice({
  name: "user",
  initialState,
  reducers: {
    login: (state, actions) => {
      return { user: actions.payload };
    },
  },
});

export default userSlice.reducer;

export const { login } = userSlice.actions;
