import axios from "axios";
import { useState } from "react";

const Write = () => {
  const [title, setTitle] = useState("");
  const [description, setDescription] = useState("");
  const [categories, setCategories] = useState([]);

  const handleSubmit = (e) => {
    e.preventDefault();
    if (!title || !description) {
      return alert("You must write anything");
    }
    const post = { title, description, categories };
    console.log(post);
  };
  return (
    <div className="write">
      <img
        src="https://images.unsplash.com/photo-1461773518188-b3e86f98242f?ixlib=rb-1.2.1&ixid=MnwxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8&auto=format&fit=crop&w=1169&q=80"
        alt="asd"
      />
      <form className="writeWrapper" onSubmit={handleSubmit}>
        <div className="form-group">
          <label>Title</label>
          <input
            type="text"
            value={title}
            onChange={(e) => setTitle(e.target.value)}
          />
        </div>
        <div className="form-group">
          <label>Description</label>
          <textarea
            cols="30"
            rows="10"
            value={description}
            onChange={(e) => setDescription(e.target.value)}
          ></textarea>
        </div>
        <div className="form-group">
          <label>Categories</label>
          <select
            name="categories"
            multiple
            value={categories}
            onChange={(e) => setCategories(e.target.value)}
          >
            <option value="HTML">HTML</option>
            <option value="JavaScript">JavaScript</option>
            <option value="CSS">CSS</option>
            <option value="React">React</option>
          </select>
        </div>
        <button className="btn">Add Post</button>
      </form>
    </div>
  );
};

export default Write;
