import styled from "styled-components";

export const Container = styled.div`
  width: 100%;
  height: calc(100vh - 60px);
  background-color: rgb(2, 33, 98);
  color: #fff;
  display: flex;
  flex-direction: column;
  align-items: center;
  padding-top: 40px;
`;

export const SearchWrapper = styled.div`
  margin-bottom: 30px;

  input {
    width: 230px;
    border: none;
    padding: 10px 5px;
  }
`;
