import { useEffect, useState } from "react";
import { useNavigate, useParams } from "react-router-dom";
import axios from "axios";
import { AiFillDelete, AiFillEdit } from "react-icons/ai";
import { useSelector } from "react-redux";

const SinglePost = () => {
  const [post, setPost] = useState({});
  const [updateMode, setUpdateMode] = useState(false);
  const { user } = useSelector((state) => state.user);

  const navigate = useNavigate();

  const id = useParams().id;

  const fetchPost = async () => {
    const res = await axios(`http://localhost:5000/post/${id}`);
    setPost(res.data);
  };

  const handleClick = async () => {
    try {
      await axios.delete(`http://localhost:5000/post/${id}`, {
        data: { user: user.username },
      });
      navigate("/");
    } catch (error) {
      console.log(error);
    }
  };

  useEffect(() => {
    fetchPost();
  }, []);

  console.log(updateMode);

  return (
    <>
      {updateMode ? (
        <div>
          <input type="text" value={post.title} />
          <textarea cols="30" rows="10" value={post.description}></textarea>
          <select name="categories" multiple value={post.categories}>
            <option value="HTML">HTML</option>
            <option value="JavaScript">JavaScript</option>
            <option value="CSS">CSS</option>
            <option value="React">React</option>
          </select>
        </div>
      ) : (
        <div className="singlePost">
          <h1>{post.title}</h1>
          <div style={{ display: "flex", gap: "20px" }}>
            <AiFillDelete
              onClick={handleClick}
              size={30}
              style={{ cursor: "pointer" }}
            />
            <AiFillEdit
              onClick={() => setUpdateMode(true)}
              size={30}
              style={{ cursor: "pointer" }}
            />
          </div>
          <p>{post.user}</p>
          <p>{post.description}</p>
          {post?.categories?.map((cat) => (
            <span key={cat}>{cat}</span>
          ))}
        </div>
      )}
    </>
  );
};

export default SinglePost;
