import React from "react";
import { Link } from "react-router-dom";
import * as S from "./Post.styled";

const Post = ({ post }) => {
  const truncDesc = (desc) => {
    if (desc.length > 100) {
      return desc.slice(0, 100) + "...";
    } else {
      return desc;
    }
  };
  return (
    <S.Container>
      <Link to={`/post/${post._id}`}>
        <h1>{post.title}</h1>
        <p>{truncDesc(post.description)} </p>
        <p className="author">
          Author: <span>{post.user}</span>
        </p>
      </Link>
    </S.Container>
  );
};

export default Post;
