import styled from "styled-components";

export const Container = styled.div`
  width: 100%;
  padding-bottom: 50px;

  img {
    width: 100%;
    height: 200px;
    object-fit: cover;
    margin-bottom: 40px;
  }
`;

export const Wrapper = styled.div`
  padding: 0 40px;
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: center;
  gap: 10px;
`;
