import styled from "styled-components";

export const Container = styled.div`
  width: 100%;
  height: auto;
  padding: 20px 40px;
`;

export const SinglePost = styled.div`
  padding-top: 30px;
  display: flex;
  flex-direction: column;
  text-align: center;
  position: relative;
`;

export const Author = styled.p`
  position: absolute;
  top: 10px;
  right: 10px;
  font-size: 16px;
`;
