import React from "react";
import { Link } from "react-router-dom";

const Post = ({ post }) => {
  return (
    <div className="post">
      <Link to={`/post/${post._id}`}>
        <h1>{post.title}</h1>
        <p>{post.description.slice(0, 5)} </p>
        <p className="author">
          Author: <span>{post.user}</span>
        </p>
      </Link>
    </div>
  );
};

export default Post;
